	private String makeSign(Map<String, String> paramsMap, String encryptKey) {
		Object[] key = paramsMap.keySet().toArray();
		Arrays.sort(key);
		StringBuilder queryString = new StringBuilder();
		for (int i = 0; i < key.length; i++) {
			String mapKey = key[i].toString();
			if (!mapKey.equals("sign")) {
				String mapValue = paramsMap.get(mapKey).toString();
				queryString.append(String.format("%s=%s&", mapKey, mapValue));
			}
		}
		queryString.append("key=" + encryptKey);
		String sign = Md5Util.getMD5(queryString.toString()).toLowerCase();
		return sign;
	}
